mapper.select:
SELECT opt_id AS optId ,opt_name AS optName ,opt_remark AS optRemark ,opt_user_id AS optUserId ,type_id AS typeId ,create_time AS createTime FROM tb_operate_record
opt_type = #{optType}
AND opt_user_id = #{optUserId}
ORDER BY opt_id DESC

mapper.update:

UPDATE tb_imgs_his SET

		imgs_new_src = #{imgsNewSrc}

WHERE imgs_id = #{imgsId}

mapper.select:

    	SELECT imgs_id AS imgsId,
		    	imgs_his_src AS imgsHisSrc,
		    	imgs_new_src AS imgsNewSrc,
		    	create_time AS createTime
		 FROM tb_imgs_his 



		 		imgs_his_src = #{imgsHisSrc}


		 		AND imgs_new_src = #{imgsNewSrc}


		    	AND imgs_id = #{imgsId}
